/* Misc */
var toolset 	= require('toolset');
var _ 			= require('underscore');


/* Plugins */
var plugins = {
	facebook:	require('./plugins/facebook.js'),
	twitter:	require('./plugins/twitter.js'),
	github:		require('./plugins/github.js'),
	instagram:	require('./plugins/instagram.js')
}


var socialApiClass = function(options) {
	var scope 	= this;
	this.init();
}
socialApiClass.prototype.init = function() {
	var provider;
	for (provider in plugins) {
		this[provider] = plugins[provider];
	}
}


module.exports = new socialApiClass();